function drawSVGToCanvas() {
        const canvas = document.getElementById('myCanvas'); 
        const ctx = canvas.getContext('2d'); 
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        
        var wrh = img.width / img.height;
        var newWidth = canvas.width;
        var newHeight = newWidth / wrh;
        if (newHeight > canvas.height) {
                newHeight = canvas.height;
                newWidth = newHeight * wrh;
        }
        var xOffset = newWidth < canvas.width ? ((canvas.width - newWidth) / 2) : 0;
        var yOffset = newHeight < canvas.height ? ((canvas.height - newHeight) / 2) : 0;
        //console.log(img.width, img.height,  xOffset, yOffset, newWidth, newHeight);

        // Draw the image on the canvas 
        ctx.drawImage(img, xOffset, yOffset, newWidth, newHeight); 
        // Clean up the URL object 
        URL.revokeObjectURL(url); 
}

function add_remove_element(ele_id) {
        
        var list = document.querySelectorAll("[data-class]");
        
        console.log(list);
        for (i=0; i<list.length;i++) {
                console.log(list[i], list[i].getAttribute('data-class'),  ele_id.getAttribute('data-class'));
                if (list[i].getAttribute('data-class') ==  ele_id.getAttribute('data-class')) {
                        list[i].setAttribute('value',"N");
                }
                
        }
        
        if (ele_id.getAttribute('value') == 'Y') {
                ele_id.setAttribute('value',"N");
        } else {
                ele_id.setAttribute('value',"Y");
        }
        
        svgString = createSVGString();

        //console.log(svgString);
        // Create a Blob from the SVG string 
        const svgBlob = new Blob([svgString], { type: 'image/svg+xml;charset=utf-8' }); 
        const url = URL.createObjectURL(svgBlob); 

        // Set the source of the image to the Blob URL 
        img.src = url; 

        drawSVGToCanvas();

}

function showOptions(type) {

        localStorage.setItem("selectedType", type);

        //Hide all the second level buttons
        var list1 = document.getElementsByClassName("second-row-buttons");
        console.log(list1);
        for (i=0; i<list1.length;i++) {
                list1[i].style.display = 'none';
        }


        console.log(type);
        var list2 = document.querySelectorAll("[data-class='" + type + "']");
        console.log(list2);
        for (i=0; i<list2.length;i++) {
                list2[i].style.display = 'inline';
        }

        var list3 = document.getElementsByClassName("color-buttons");
        console.log(list3);
        
        for (i=0; i<list3.length;i++) {
                console.log(list3[i]);
                list3[i].style.display = 'inline';
                list3[i].addEventListener("change", changeFillColor, false);
        }

        
}

function changeFillColor (event) {

        colorFill = event.target.value;

        type = localStorage.getItem("selectedType");
        console.log(type);

        typeColorFillVar = "colorFill-" + type;
        localStorage.setItem(typeColorFillVar, colorFill);


        svgString = createSVGString();

        //console.log(svgString);
        // Create a Blob from the SVG string 
        const svgBlob = new Blob([svgString], { type: 'image/svg+xml;charset=utf-8' }); 
        const url = URL.createObjectURL(svgBlob); 

        // Set the source of the image to the Blob URL 
        img.src = url; 

        e.stopPropagation();
        drawSVGToCanvas();

}

function download() {
        const canvas = document.getElementById('myCanvas'); 
        let pngUrl = canvas.toDataURL("image/png");
        // To download the image
        let downloadLink = document.createElement("a");
        downloadLink.href = pngUrl;
        downloadLink.download = "image.png";
        downloadLink.click();
}

